"use strict";window.addEventListener("load",()=>{const e=document.querySelectorAll(".accordion"),t=document.querySelectorAll(".accordion__title");for(const t of e)t.style.height=t.firstElementChild.offsetHeight+"px";t.forEach((t,i)=>t.addEventListener("click",i=>{const s=t.parentNode,o=t.nextElementSibling;i.preventDefault();for(const t of e)t.style.height=t.firstElementChild.offsetHeight+"px",t.firstElementChild.classList.remove("active");s.offsetHeight===t.offsetHeight?(s.style.height=s.offsetHeight+o.offsetHeight+"px",t.classList.add("active")):(t.classList.remove("active"),s.style.height=t.offsetHeight+"px")}))});